function LoginForm() {
    const [inputs, setInputs] = useState('')
    // const [username, setUsername] = useState('')
    // const [password, setPassword] = useState('')

    const handleInputs = (event) => {
        let name = event.target.name
        let value = event.target.value
        setInputs(values => (
            { ...values, [name]: value }
        ))
    }

    const handleSubmit = (event) => {
        event.preventDefault()
        // alert(`Name: ${username} Password: ${password}`)
        alert(`Inputs: ${inputs}`)
    }

    return (
        <form onSubmit={handleSubmit}>
            <h2>Login Page</h2>
            <label>Username :</label><br />
            <input type="text" id="username" value={inputs.username} onChange={(e) => setUsername(e.target.value)} /><br />
            <label>Password :</label><br />
            <input type="text" id="password" value={inputs.password} onChange={(e) => setPassword(e.target.value)} /><br />
            <input type="submit" value="submit" />
        </form>
    )
}

export default LoginForm
